import * as React from 'react';

import {
  FasterImageView,
  clearCache,
  prefetch,
} from '@candlefinance/faster-image';
import {
  Button,
  DevSettings,
  Dimensions,
  FlatList,
  Platform,
  SafeAreaView,
  StyleSheet,
} from 'react-native';

const size = Dimensions.get('window').width / 3;
const imageURLs = Array.from(
  { length: 100 },
  (_, i) => `https://picsum.photos/200/200?random=${4000 + i}`
);

if (__DEV__ && Platform.OS === 'ios') {
  DevSettings.addMenuItem('Clear Cache', async () => {
    const result = await clearCache();
    console.log('cache cleared', result);
  });
  DevSettings.addMenuItem('Prefetch', async () => {
    const result = await prefetch(['https://picsum.photos/200/200?random=0']);
    console.log('prefetched', result);
  });
}

export default function App() {
  const [data, setData] = React.useState(imageURLs);
  return (
    <SafeAreaView style={styles.container}>
      <Button
        title="Clear Cache"
        onPress={() => {
          clearCache()
            .then((result) => {
              console.log('cache cleared', result);
              setData([]);
            })
            .then(() => {
              setData(imageURLs);
            });
        }}
      />
      <Button
        title="Prefetch"
        onPress={() => {
          prefetch(['https://picsum.photos/200/200?random=0']).then((result) =>
            console.log('prefetched', result)
          );
        }}
      />
      {/* <FasterImageView
        onError={(event) => console.warn(event.nativeEvent.error)}
        style={styles.image}
        onSuccess={(event) => {
          console.log(event.nativeEvent);
        }}
        source={{
          transitionDuration: 0.3,
          // borderRadius: Platform.OS === 'android' ? size * 2 : (size - 16) / 2,
          cachePolicy: 'discWithCacheControl',
          showActivityIndicator: true,
          resizeMode: 'contain',
          base64Placeholder:
            '',
          url: 'https://picsum.photos/300/300?random=5',
        }}
      /> */}
      <FlatList
        keyExtractor={(item) => item}
        data={data}
        numColumns={3}
        columnWrapperStyle={styles.column}
        getItemLayout={(_, index) => ({
          length: size - 16,
          offset: (size - 16) * index,
          index,
        })}
        renderItem={({ item }) => (
          <FasterImageView
            onError={(event) => console.warn(event.nativeEvent.error)}
            style={styles.image}
            onSuccess={(event) => {
              console.log(event.nativeEvent);
            }}
            source={{
              transitionDuration: 0.3,
              borderTopLeftRadius:
                Platform.OS === 'android' ? size : (size - 16) / 2,
              borderBottomRightRadius:
                Platform.OS === 'android' ? size / 1.5 : (size - 16) / 3,
              cachePolicy: 'discWithCacheControl',
              showActivityIndicator: true,
              url: item,
              grayscale: 1,
            }}
          />
        )}
      />
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  sep: { height: 24 },
  image: {
    width: size - 16,
    height: size - 16,
    borderTopLeftRadius: (size - 16) / 2,
    borderBottomRightRadius: (size - 16) / 2,
    overflow: 'hidden',
    backgroundColor: 'white',
  },
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
  },
  column: {
    justifyContent: 'space-between',
    marginVertical: 8,
    marginHorizontal: 8,
    gap: 8,
  },
});
